// popup.ts

import { recognizeImage, parseAIResult, defaultAISettings } from "./ai.js";
import { blobToDataUrl, scaleImage } from "./image.js";
import { modelNames } from "./models.js";

// 添加showOpenFilePicker的类型定义

console.log("Marking extension popup loaded");

// 获取DOM元素
const statusText = document.getElementById(
  "statusText"
) as HTMLParagraphElement;
const modelSelect = document.getElementById("modelSelect") as HTMLSelectElement;
const promptTextarea = document.getElementById(
  "promptTextarea"
) as HTMLTextAreaElement;
const selectFileBtn = document.getElementById(
  "selectFileBtn"
) as HTMLButtonElement;
const doubaoKeyInput = document.getElementById(
  "doubaoKeyInput"
) as HTMLInputElement;
const cardImage = document.getElementById("cardImage") as HTMLImageElement;
const result = document.getElementById("result") as HTMLDivElement;
const markButton = document.getElementById("markButton") as HTMLButtonElement;
const imageInfo = document.getElementById("imageInfo") as HTMLSpanElement;

interface AISettings {
  aiModel?: string;
  aiPrompt?: string;
}

interface APIKeys {
  doubaoKey?: string;
  glmKey?: string;
  hunyuanKey?: string;
  qwenKey?: string;
}

function loadAISettings() {
  // 清空现有选项
  modelSelect.innerHTML = "";

  // 动态生成模型选项
  modelNames.forEach((model) => {
    const option = document.createElement("option");
    option.value = model;
    option.textContent = model;
    modelSelect.appendChild(option);
  });

  chrome.storage.local.get(
    ["aiModel", "aiPrompt", "apiKeys", "cardImageDataUrl"],
    (result) => {
      if (chrome.runtime.lastError) {
        console.error("Error reading AI settings:", chrome.runtime.lastError);
        return;
      }

      const settings = result as AISettings;
      const apiKeys = result.apiKeys as APIKeys;
      console.log(settings);
      modelSelect.value = settings.aiModel || defaultAISettings.model;
      promptTextarea.value = settings.aiPrompt || defaultAISettings.prompt;
      console.log(promptTextarea.value);
      if (apiKeys?.doubaoKey) {
        doubaoKeyInput.value = apiKeys.doubaoKey;
      }

      const imageUrl =
        "data:image/webp;base64,";
      // 使用保存的图片URL，如果没有则使用默认值
      const savedImageUrl = result.cardImageDataUrl as string;
      if (savedImageUrl) {
        cardImage.src = savedImageUrl;
      } else {
        cardImage.src = imageUrl;
      }
      console.log("AI settings loaded from storage");
    }
  );
}

// 保存API Key函数
function saveApiKey() {
  const doubaoKey = doubaoKeyInput.value;

  // 获取现有API Keys，然后更新doubaoKey
  chrome.storage.local.get(["apiKeys"], (result) => {
    const existingKeys = (result.apiKeys as APIKeys) || {};
    const updatedKeys: APIKeys = {
      ...existingKeys,
      doubaoKey,
    };

    chrome.storage.local.set({ apiKeys: updatedKeys }, () => {
      if (chrome.runtime.lastError) {
        console.error("Error saving API Key:", chrome.runtime.lastError);
        return;
      }
      console.log("API Key saved to storage:", updatedKeys);
    });
  });
}

// 保存AI设置函数
function saveAISettings() {
  const model = modelSelect.value;
  const prompt = promptTextarea.value;

  chrome.storage.local.set({ aiModel: model, aiPrompt: prompt }, () => {
    if (chrome.runtime.lastError) {
      console.error("Error saving AI settings:", chrome.runtime.lastError);
      return;
    }

    console.log("AI settings saved to storage");
  });
}

function mark() {
  recognizeImage(cardImage.src).then(
    (res) => {
      console.log("AI recognition result:", res);
      result.innerText = parseAIResult(res);
      result.style.color = "black";
    },
    (error) => {
      console.error("AI recognition failed:", error);
      result.innerText = `AI recognition failed: ${error}`;
      result.style.color = "red";
    }
  );
}
// 从剪贴板粘贴图片
const pasteImageFromClipboard = async (event?: ClipboardEvent) => {
  // 优先使用事件中的clipboardData
  if (event?.clipboardData) {
    const items = event.clipboardData.items;

    // 遍历剪贴板项
    for (const item of items) {
      // 检查是否是图片类型
      if (item.type.startsWith("image/")) {
        // 获取图片blob
        const blob = item.getAsFile();
        if (blob) {
          const _dataUrl = await blobToDataUrl(blob);
          const dataUrl = await scaleImage(_dataUrl);
          // 保存图片dataUrl到storage
          chrome.storage.local.set({ cardImageDataUrl: dataUrl }, () => {
            if (chrome.runtime.lastError) {
              console.error(
                "Error saving image data URL:",
                chrome.runtime.lastError
              );
              return;
            }
            console.log("Image data URL saved to storage");
          });
          // 显示图片
          cardImage.src = dataUrl;

          return;
        }
      }
    }
  }
};

// 模型选择变化事件监听
modelSelect.addEventListener("change", saveAISettings);
// 提示词变化事件监听
promptTextarea.addEventListener("blur", saveAISettings);
// API Key变化事件监听
doubaoKeyInput.addEventListener("change", saveApiKey);
markButton.addEventListener("click", mark);

// 添加键盘快捷键监听，使用Ctrl+V粘贴图片
document.addEventListener("paste", pasteImageFromClipboard);
cardImage.addEventListener("load", () => {
  imageInfo.innerText = `图片尺寸: ${cardImage.naturalWidth}x${cardImage.naturalHeight}`;
});

// 创建显示图片尺寸的函数

// 初始加载
loadAISettings();
